mapper.select:
SELECT 
	c.*,exp_date,doApply,category_area,category_type
FROM
	coupon_target ct
JOIN
	coupon c
ON
	ct.cpno = c.cpno
WHERE
	(
	id=#{pname}
and
	category_area=#{category_area}
	)
or
	(
	id=#{pname}
and
	category_type=#{category_type}
	)
or
	(
	id=#{pname}
and
	category_area=0
	)
;

mapper.update:
UPDATE cart
SET cpno=#{cpno}

,doOrder=false

WHERE id=#{id} 

and 		
	pno=#{pno}

UPDATE coupon_target SET
doApply=false
doApply=true
WHERE id=#{id}
and category_area=#{b_quantity} and category_type=#{add_date}
and category_area=0
