mapper.select:
select *
from(
	select @rownum:=@rownum+1 rownum, i.*
    from inquiry i,(select @rownum:=0) as tmp
	where pno=#{search} 
    order by ino desc
) as inquirylist

where rownum > (#{pageNum}-1)*#{amount} and rownum <= #{pageNum}*#{amount}

mapper.insert:
  		update inquiry
		set answer = #{answer}
		where ino=#{ino}
